ion: all 0.25s ease-in;\n}\n#mobile-menu > ul > li.is-active, #mobile-menu > ul > li.router-link-exact-active {\n  background-color: #2176d2;\n}\n#mobile-menu > ul > li > a {\n  color: white;\n  font-size: 1.125rem;\n  padding: 1rem 3rem;\n  display: block;\n  position: relative;\n}\n#mobile-menu > ul > li > a.is-active, #mobile-menu > ul > li > a.router-link-exact-active {\n  background-color: #2176d2;\n  font-weight: 600;\n}\n#mobile-menu > ul > li > a.has-submenu:after {\n  position: absolute;\n  right: 1rem;\n  top: 50%;\n  transform: translateY(-50%);\n  width: 20px;\n  height: 2px;\n  background-color: white;\n  display: block;\n  transition: all 0.25s ease-in;\n  content: \"\";\n}\n#mobile-menu > ul > li > a.has-submenu:before {\n  position: absolute;\n  right: 1rem;\n  top: 50%;\n  transform: translateY(-50%) rotate(-90deg);\n  width: 20px;\n  height: 2px;\n  background-color: white;\n  display: block;\n  content: \"\";\n  transition: all 0.25s ease-in;\n  opacity: 1;\n}\n#mobile-menu > ul > li.opened {\n  border-top: 2px solid #daedfe;\n  border-bottom: 2px solid #daedfe;\n}\n#mobile-menu > ul > li.opened > a {\n  font-weight: 600;\n}\n#mobile-menu > ul > li.opened > a:before {\n  transform: translateY(-50%) rotate(45deg);\n}\n#mobile-menu > ul > li.opened > a:after {\n  transform: translateY(-50%) rotate(-45deg);\n}\n#mobile-menu > ul > li.opened > ul {\n  opacity: 1;\n  height: auto;\n  padding: 0.75rem 0;\n}\n#mobile-menu > ul > li > ul {\n  transition: all 0.25s ease-in-out;\n  opacity: 0;\n  height: 0;\n  overflow: hidden;\n  width: 100%;\n  padding: 0;\n  background-color: #0c3664;\n}\n#mobile-menu > ul > li > ul > li {\n  list-style-type: none;\n  border: 0;\n}\n#mobile-menu > ul > li > ul > li.is-active, #mobile-menu > ul > li > ul > li.router-link-exact-active {\n  background-color: #2176d2;\n}\n#mobile-menu > ul > li > ul > li > a {\n  color: white;\n  font-size: 1.125rem;\n  padding: 1rem 1rem;\n  display: block;\n  margin: 0 2rem;\n}\n#mobile-menu > ul > li > ul > li > a.is-active, #mobile-menu > ul > li > ul > li > a.router-link-exact-active {\n  background-color: #2176d2;\n  font-weight: 600;\n}\n#mobile-menu > ul > li > ul > li.opened > a {\n  background-color: #2176d2;\n  font-weight: 600;\n}\n#mobile-menu > ul > li > ul > li.opened > ul {\n  opacity: 1;\n  height: auto;\n}\n#mobile-menu > ul > li > ul > li > ul {\n  transition: all 0.25s ease-in-out;\n  opacity: 0;\n  height: 0;\n  overflow: hidden;\n  width: 100%;\n  padding: 0;\n}\n#mobile-menu > ul > li > ul > li > ul > li > a {\n  color: white;\n  font-size: 1.125rem;\n  display: block;\n  padding: 1rem;\n  margin: 0 2rem;\n}\n#mobile-menu > ul > li > ul > li > ul > li > a.is-active, #mobile-menu > ul > li > ul > li > ul > li > a.router-link-exact-active {\n  background-color: #2176d2;\n  font-weight: 600;\n}\n#mobile-menu > ul > li > ul > li > ul > li > a.is-active:before, #mobile-menu > ul > li > ul > li > ul > li > a.router-link-exact-active:before {\n  background-color: #daedfe;\n}\n#mobile-menu > ul > li > ul > li > ul > li > a:before {\n  border-radius: 50%;\n  border: 1px solid #daedfe;\n  width: 10px;\n  height: 10px;\n  content: \"\";\n  display: inline-block;\n  margin-left: 0.75rem;\n  margin-right: 0.75rem;\n}\n\n.slide-in-enter-active {\n  transition: all 0.2s ease-in;\n}\n\n.slide-in-leave-active {\n  transition: all 0.2s ease-out;\n}\n\n.slide-in-enter, .slide-in-leave-to {\n  transform: translateX(-100%);\n  opacity: 0;\n}\n\n/*# sourceMappingURL=MobileNav.vue.map */","<template>\n  <div\n    id=\"mobile-nav\"\n    aria-label=\"Mobile Nav\"\n    :class=\"{ 'opened': mNavOpened }\"\n  >\n    <a\n      href=\"#\"\n      class=\"icon is-size-2\"\n      aria-label=\"Mobile Nav\"\n      @click.prevent=\"mobileNavToggle\"\n    >\n      <i\n        v-show=\"!mNavOpened\"\n        title=\"Open Mobile Nav\"\n        class=\"fa fa-bars\"\n      />\n      <i\n        v-show=\"mNavOpened\"\n        title=\"Close Mobile Nav\"\n        class=\"f